mapper.update:
UPDATE QNABOARD
   SET QAHIT = QAHIT + 1
 WHERE QANUM = #{qanum}

mapper.insert:
	INSERT INTO QNABOARD
           (QANUM, MID, QACAT, QATITLE, QACONTENT
          , QAPW, QAGROUP, QASTEP, QAINDENT, QAIP)
    VALUES (QNABOARD_SEQ.NEXTVAL
          , #{mid}
          , #{qacat}
          , #{qatitle}
          , #{qacontent}
          , #{qapw}
          , QNABOARD_SEQ.CURRVAL
          , 0
          , 0
          , #{qaip})

mapper.update:
UPDATE QNABOARD 
   SET QACAT = #{qacat}
     , QATITLE = #{qatitle}
     , QACONTENT = #{qacontent}
     , QAIP = #{qaip}
 WHERE QANUM = #{qanum}

	UPDATE QNABOARD 
       SET QASTEP = QASTEP + 1
     WHERE QAGROUP = #{qagroup}
       AND QASTEP > #{qastep}

mapper.insert:
INSERT INTO QNABOARD
            (QANUM, ADID, QACAT, QATITLE, QACONTENT
           , QAPW, QAGROUP, QASTEP, QAINDENT, QAIP)
     VALUES (QNABOARD_SEQ.NEXTVAL
           , #{adid}
           , #{qacat}
           , #{qatitle}
           , #{qacontent}
           , #{qapw}
           , #{qagroup}
           , #{qastep} +1
           , #{qaindent} +1
           , #{qaip})
