mapper.select:
SELECT * FROM (SELECT ROWNUM RN, RES.* FROM (SELECT DISTINCT TR.*, T.* FROM TICKET_RES TR , TICKET_RESDETAIL TRD , (SELECT DISTINCT TNAME, TIMG FROM TICKET) T WHERE TR.TRCODE = TRD.TRCODE AND TRDNAME = T.TNAME
AND MID = #{mid}
ORDER BY TR.TRCODE DESC) RES) WHERE RN BETWEEN #{startRow} AND #{endRow}

		SELECT RES.*, MNAME, MTEL, MMAIL FROM TICKET_RES RES, MEMBER
            WHERE RES.MID = MEMBER.MID
              AND TRCODE = #{trcode}

mapper.update:
UPDATE TICKET_RES 
   SET TRRESULT = 1
 WHERE TRCODE = #{trcode}
